<?php

namespace CarBundle\Repository;

use Doctrine\ORM\EntityRepository;

/**
 * CarRepository
 *
 * This class was generated by the Doctrine ORM. Add your own custom
 * repository methods below.
 */
class CarRepository extends EntityRepository
{
    /**
     * Returns all cars with details
     *
     * @return array
     */
    public function findCarsWithDetails(): array
    {
        $queryBuilder = $this->createQueryBuilder('c');

        $queryBuilder->select('c', 'make', 'model')
            ->join('c.make', 'make')
            ->join('c.model', 'model');

        return $queryBuilder->getQuery()->getResult();
    }

    /**
     * Return car entity with specified id
     *
     * @param int $id
     *
     * @return mixed
     * @throws \Doctrine\ORM\NonUniqueResultException
     * @throws \Doctrine\ORM\NoResultException
     */
    public function findCarWithDetailsById(int $id)
    {
        $queryBuilder = $this->createQueryBuilder('c');

        $queryBuilder->select('c', 'make', 'model')
            ->join('c.make', 'make')
            ->join('c.model', 'model')
            ->where('c.id = :id')
            ->setParameter('id', $id);

        return $queryBuilder->getQuery()->getSingleResult();
    }
}
